}

package com.themcraft.lobby.Listeners;

import org.bukkit.Sound;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.player.PlayerJoinEvent;
import org.bukkit.inventory.ItemStack;
import org.bukkit.Material;
import org.bukkit.inventory.meta.ItemMeta;
import org.bukkit.Bukkit;
import org.bukkit.scoreboard.Scoreboard;
import org.bukkit.scoreboard.ScoreboardManager;
import org.bukkit.scoreboard.Objective;
import org.bukkit.scoreboard.DisplaySlot;

import java.util.Arrays;

public class joinListener implements Listener {
    @EventHandler
    public void onPlayerJoin(PlayerJoinEvent event) {
        Player player = event.getPlayer();
        event.setJoinMessage(null);
        player.sendTitle("§e§lWillkommen", "§a§lauf GottHub!");
        player.playSound(player.getLocation(), Sound.BLOCK_NOTE_BLOCK_PLING, 1.0f, 1.0f);
        player.playSound(player.getLocation(), Sound.BLOCK_NOTE_BLOCK_PLING, 1.0f, 1.5f);
        player.playSound(player.getLocation(), Sound.BLOCK_NOTE_BLOCK_PLING, 1.0f, 1.9f);

        // Inventory bereinigen und Lobby-Items geben
        player.getInventory().clear();
        ItemStack compass = new ItemStack(Material.COMPASS);
        ItemMeta meta = compass.getItemMeta();
        if (meta != null) {
            meta.setDisplayName("§eServerauswahl");
            meta.setLore(Arrays.asList("§7Rechtsklick öffnet das Menü"));
            compass.setItemMeta(meta);
        }
        player.getInventory().setItem(0, compass);

        // Einfaches Scoreboard setzen
        ScoreboardManager manager = Bukkit.getScoreboardManager();
        if (manager != null) {
            Scoreboard board = manager.getNewScoreboard();
            Objective obj = board.registerNewObjective("lobby", "dummy", "§6§lLobby");
            obj.setDisplaySlot(DisplaySlot.SIDEBAR);
            obj.getScore("Spieler: §a" + player.getName()).setScore(1);
            player.setScoreboard(board);
        }
    }
}

